import React, { useState, useEffect, useRef } from "react";
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid } from "recharts";

const RealTimeChart = () => {
  const [chartData, setChartData] = useState([]);
  const wsRef = useRef(null);

  const connectWebSocket = () => {
    console.log("üîÑ ƒêang k·∫øt n·ªëi WebSocket...");
    const ws = new WebSocket("ws://127.0.0.1:5001"); // D√πng 127.0.0.1 thay v√¨ localhost

    ws.onopen = () => {
      console.log("‚úÖ WebSocket ƒë√£ k·∫øt n·ªëi!");
    };

    ws.onerror = (error) => {
      console.error("‚ùå L·ªói k·∫øt n·ªëi WebSocket:", error);
    };

    ws.onclose = () => {
      console.warn("‚ö†Ô∏è WebSocket b·ªã ƒë√≥ng! ƒêang th·ª≠ k·∫øt n·ªëi l·∫°i sau 3s...");
      setTimeout(connectWebSocket, 3000);
    };

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        console.log("üì© D·ªØ li·ªáu nh·∫≠n ƒë∆∞·ª£c:", data);

        setChartData((prev) => [
          ...prev.slice(-20),
          { time: new Date().toLocaleTimeString(), price: parseFloat(data.price) },
        ]);
      } catch (error) {
        console.error("‚ùå L·ªói x·ª≠ l√Ω d·ªØ li·ªáu WebSocket:", error);
      }
    };

    wsRef.current = ws;
  };

  useEffect(() => {
    connectWebSocket();

    return () => {
      if (wsRef.current) {
        wsRef.current.close();
        console.log("üî¥ ƒê√≥ng WebSocket khi component b·ªã unmount");
      }
    };
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold">üìà Bi·ªÉu ƒë·ªì gi√° real-time</h2>
      <LineChart width={600} height={300} data={chartData}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="time" />
        <YAxis domain={["auto", "auto"]} />
        <Tooltip />
        <Line type="monotone" dataKey="price" stroke="blue" strokeWidth={2} />
      </LineChart>
    </div>
  );
};

export default RealTimeChart;
