import React, { useState, useEffect } from "react";
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid } from "recharts";

const RealTimeChart = () => {
  const [chartData, setChartData] = useState([]);
  const [ws, setWs] = useState(null);

  useEffect(() => {
    const newWs = new WebSocket("ws://localhost:5001");

    newWs.onopen = () => {
      console.log("✅ WebSocket đã kết nối!");
    };

    newWs.onerror = (error) => {
      console.error("❌ Lỗi kết nối WebSocket:", error);
    };

    newWs.onclose = () => {
      console.warn("⚠️ WebSocket đã bị đóng! Thử kết nối lại...");
      setTimeout(() => setWs(new WebSocket("ws://localhost:5001")), 3000);
    };

    newWs.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setChartData((prev) => [...prev.slice(-20), { time: new Date().toLocaleTimeString(), price: parseFloat(data.price) }]);
    };

    setWs(newWs);
    return () => newWs.close();
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold">📈 Biểu đồ giá real-time</h2>
      <LineChart width={600} height={300} data={chartData}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="time" />
        <YAxis domain={["auto", "auto"]} />
        <Tooltip />
        <Line type="monotone" dataKey="price" stroke="blue" strokeWidth={2} />
      </LineChart>
    </div>
  );
};

export default RealTimeChart;
