import React, { useState, useEffect } from "react";
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid } from "recharts";

const RealTimeChart = () => {
  const [chartData, setChartData] = useState([]);

  useEffect(() => {
    const ws = new WebSocket("ws://localhost:5001"); // Káº¿t ná»‘i WebSocket

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setChartData((prev) => [...prev.slice(-20), { time: new Date().toLocaleTimeString(), price: parseFloat(data.price) }]);
    };

    return () => ws.close();
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold">ðŸ“ˆ Biá»ƒu Ä‘á»“ giÃ¡ real-time</h2>
      <LineChart width={600} height={300} data={chartData}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="time" />
        <YAxis domain={["auto", "auto"]} />
        <Tooltip />
        <Line type="monotone" dataKey="price" stroke="#8884d8" strokeWidth={2} />
      </LineChart>
    </div>
  );
};

export default RealTimeChart;
